import React, { useRef, useState } from "react";
import EntryList from "./EntryList";



                  
const EntryInfo = (localStorage.getItem("entryDetails")) ? JSON.parse(localStorage.getItem("entryDetails")) : [] 


const EntryForm = () => {
   

    const [EntryList, setEntryList] = useState(EntryInfo);
    console.log(EntryList)

    const dateRef = useRef(); //using react to get a selector without dom using this code "useRef()" method 
    const messageRef = useRef(); 

    const onFormSubmit = (e) => {
      e.preventDefault();

     

    //   setFirstName(fNameRef.current.value) //this line of code to assign in setters and get the value
    //   setLastName(lNameRef.current.value) 
    //   setEmail(emailRef.current.value) 
    //   setMessage(messageRef.current.value)
      
      const entryObject = { // object literal assign an property and value from useState
          id: Date.now(),
          date: dateRef.current.value,    //get from the use state the value firstname
          msg: messageRef.current.value,
      }
      console.log(entryObject)
       
    //   contactListMap.push(contactObject)
      setEntryList([...EntryList, entryObject]);
                                            //Json.stringify convert into a json string and can see in dev tools application tab and local storage       
      localStorage.setItem("contactDetails", JSON.stringify(EntryList));//use an local storage api
      console.log(EntryList);


      dateRef.current.value = "";   //if submit clear the text in this code
      messageRef.current.value = "";

    

     
  };

  return (
    <div>
        <h4>My Coding Journal</h4>
        <form onSubmit={onFormSubmit}>
            <label>Thoughts for the Day</label>
            <br/>
            <label>Date</label>
            <input type="text" name="text" ref={dateRef} />
            <br/>
            <textarea 
                name="message" 
                cols="30" 
                rows="10" 
                ref={messageRef}>
            </textarea>
            <br />
            <input type="submit" value="submit" name="submit" />
        </form>
        <EntryList entry={EntryList}/>
    </div>
  )
}

export default EntryForm
