import React, { useRef, useState } from "react";
import ContactList from "./ContactList"


                    //json.parse return a value //pinaikli if statement using ternary operations ? sa symbol na eto true sa : ay false at empty array
const contactInfo = (localStorage.getItem("contactDetails")) ? JSON.parse(localStorage.getItem("contactDetails")) : [] //this variable save here what in the local storage if refresh will see the cache


const ContactForm = () => {
   
    // const [firstName, setFirstName] = useState("");//use a useState here if have "" inside a useState controled
    // const [lastName, setLastName] = useState(""); //add with value in input
    // const [email, setEmail] = useState("");
    // const [message, setMessage] = useState("");

    const [contactList, setContactList] = useState(contactInfo);

    // const contactListMap = contactList; //assign a new variable variable to contactlist cannot edit directly
    
    
    console.log(contactList)

    const dat = useRef(); //using react to get a selector without dom using this code "useRef()" method
    const lNameRef = useRef(); //add in input as a ref and assign them all
    const emailRef = useRef(); 
    const messageRef = useRef(); 

    const onFormSubmit = (e) => {
      e.preventDefault();

     

    //   setFirstName(fNameRef.current.value) //this line of code to assign in setters and get the value
    //   setLastName(lNameRef.current.value) 
    //   setEmail(emailRef.current.value) 
    //   setMessage(messageRef.current.value)
      
      const contactObject = { // object literal assign an property and value from useState
          id: Date.now(),
          date: fNameRef.current.value,    //get from the use state the value firstname
          lname: lNameRef.current.value,   //using useRef to get the value
          emailAd: emailRef.current.value,
          msg: messageRef.current.value,
      }
      console.log(contactObject)
       
    //   contactListMap.push(contactObject)
      setContactList([...contactList, contactObject]);
                                            //Json.stringify convert into a json string and can see in dev tools application tab and local storage       
      localStorage.setItem("contactDetails", JSON.stringify(contactList));//use an local storage api
      console.log(contactList);


      fNameRef.current.value = "";   //if submit clear the text in this code
      lNameRef.current.value = "";
      emailRef.current.value = "";
      messageRef.current.value = "";

    

     
  };

  return (
    <div>
        <h4>Contact Form</h4>
        <form onSubmit={onFormSubmit}>
            <label>Thoughts for the Day</label>
            <input type="date" name="date" ref={lNameRef} />
            <br />
            <label>Email</label>
            <input type="text" name="email" ref={emailRef} />
            <br />
            <label>Message</label>
            <textarea 
                name="message" 
                cols="30" 
                rows="10" 
                ref={messageRef}>

            </textarea>
            <br />
            <input type="submit" value="submit" name="submit" />
        </form>
        <ContactList contacts={contactList}/>
    </div>
  )
}

export default ContactForm
